mapper.select:
select

from audit_reco_status


        And group_id = #{inLongGroupId,jdbcType=VARCHAR}


        And stream_id = #{inLongStreamId,jdbcType=VARCHAR}


        And is_delete = #{isDelete,jdbcType=TINYINT}


        And audit_status = #{auditStatus,jdbcType=VARCHAR}


        and partition_day = #{partitionDay,jdbcType=VARCHAR}


        and partition_value = #{partitionValue,jdbcType=VARCHAR}
